import React from "react";
import { SelectValue } from "../Select/SelectProps";
export type MultipleSelectValue<Type> = {
    name?: string;
    value?: Type;
};
export type MultipleSelectPropsType<Type> = {
    /**
     * Лейбл для select
     */
    label: string;
    /**
     * Опции доступные для выбора в select
     */
    options: Array<SelectValue<Type>>;
    /**
     * zIndex всплывающего окна
     */
    zIndex: number;
    /**
     * Максимальное количество опций, которые можно выбрать
     */
    maxSelectedOptions?: number;
    /**
     * Имя элемента в DOM
     */
    name?: string;
    /**
     * Событие вызываемое при выборе опции
     * @param option - массив выбранных опций
     */
    onChange?: (option: Array<SelectValue<Type>>) => void;
    /**
     * Значение по умолчанию для select
     */
    defaultValue?: Array<SelectValue<Type>>;
    /**
     *
     */
    wrapperStyles?: React.CSSProperties;
    /**
     * стили выпадающей шторки
     */
    dropdownStyles?: React.CSSProperties;
    /**
     * Дополнительные элементы сверху от пунктов выборщика (в выпадающей шторке)
     */
    additionalTopContent?: React.ReactNode;
    /**
     * Дополнительные стили для элементов сверху от пунктов выборщика (в выпадающей шторке
     */
    additionalBottomContent?: React.ReactNode;
    /**
     * Дополнительные элементы снизу от пунктов выборщика (в выпадающей шторке)
     */
    additionalTopContentStyles?: React.CSSProperties;
    /**
     * Дополнительные стили для элементов снизу от пунктов выборщика (в выпадающей шторке
     */
    additionalBottomContentStyles?: React.CSSProperties;
};
